import { Component } from '@angular/core';

@Component({
  selector: 'app-supply-catalog',
  templateUrl: './catalog.component.html',
  styleUrls: ['./catalog.component.css']
})
export class SupplyCatalogComponent {
  products = [
    {
      name: 'Desktop Computer',
      category: 'IT Equipment',
      vendor: 'Vendor One',
      price: 792.38,
      image: 'data:image/jpeg;base64,'
    },
    {
      name: 'LCD Monitor',
      category: 'IT Equipment',
      vendor: 'Vendor One',
      price: 117.00,
      image: 'data:image/jpeg;base64,'
    },
    {
      name: 'Laptop Computer',
      category: 'IT Equipment',
      vendor: 'Vendor One',
      price: 1471.12,
      image: 'data:image/jpeg;base64,'
    },
    {
      name: 'LCD Monitor',
      category: 'IT Equipment',
      vendor: 'Vendor One',
      price: 117.00,
      image: 'data:image/jpeg;base64,'
    },
    {
      name: 'Mouse',
      category: 'IT Equipment',
      vendor: 'Vendor One',
      price: 1471.12,
      image: 'data:image/jpeg;base64,'
    },
    {
      name: 'Keyboard',
      category: 'IT Equipment',
      vendor: 'Vendor One',
      price: 792.38,
      image: 'data:image/jpeg;base64,'
    },
   
  ];

  cart: { name: string, price: number, qty: number }[] = [];


  addToCart(product: any, qtyInput: any) {
    const qty = parseInt(qtyInput.value, 10) || 1;
    const existingProduct = this.cart.find(item => item.name === product.name);

    if (existingProduct) {
      existingProduct.qty += qty;
    } else {
      this.cart.push({ name: product.name, price: product.price, qty });
    }
    qtyInput.value = '';  
  }

  
  clearCart() {
    this.cart = [];
  }

  
  checkOut() {
    if (this.cart.length > 0) {
      alert(`Checking out with ${this.cart.length} items.`);
      this.clearCart();
    } else {
      alert('Your cart is empty!');
    }
  }
  viewCart() {
    if (this.cart.length > 0) {
      let cartContent = this.cart.map(item => `${item.name} (x${item.qty})`).join(', ');
      alert(`Your cart contains: ${cartContent}`);
    } else {
      alert('Your cart is empty!');
    }
  }
  
}
